package com.abdurashidov.mvc_patternimport java.util.Observableclass Model:Observable() {    val list:MutableList<Int>    init {        list=ArrayList(3)        list.add(0)        list.add(0)        list.add(0)    }    fun getValueAtIndex(the_index:Int):Int=list[the_index]    fun setValueAtIndex(the_index: Int){        list[the_index]+=1        setChanged()        notifyObservers()    }}